contracts/RepaymentManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

interface IProjectRegistry {
    enum ProjectStatus { 
        PENDING, APPROVED, FUNDING, FUNDED, ACTIVE, COMPLETED, CANCELLED 
    }
    
    struct Project {
        uint256 id;
        address artist;
        string title;
        string description;
        string demoUri;
        uint256 fundingGoal;
        uint256 fundingDeadline;
        uint256 repaymentDeadline;
        uint256 interestRate;
        ProjectStatus status;
        bool allowsRoyaltyRepayment;
        uint256 createdAt;
        uint256 approvedAt;
    }
    
    function getProject(uint256 _projectId) external view returns (Project memory);
    function updateProjectStatus(uint256 _projectId, ProjectStatus _newStatus) external;
}

interface ILoanEscrow {
    struct Investment {
        address investor;
        uint256 amount;
        uint256 timestamp;
    }
    
    function getEscrowData(uint256 _projectId) 
        external 
        view 
        returns (
            uint256 totalRaised,
            uint256 goalAmount,
            bool fundsReleased,
            bool fundsRefunded,
            uint256 investorCount
        );
    
    function getInvestorData(uint256 _projectId, address _investor) 
        external 
        view 
        returns (uint256);
}

contract RepaymentManager is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    
    IProjectRegistry public projectRegistry;
    ILoanEscrow public loanEscrow;
    
    // Chainlink price feed para conversões
    AggregatorV3Interface public priceFeed;
    
    struct RepaymentSchedule {
        uint256 projectId;
        uint256 totalOwed;          // Total devido (principal + juros)
        uint256 totalPaid;          // Total já pago
        uint256 nextPaymentDue;     // Data do próximo pagamento
        uint256 monthlyPayment;     // Pagamento mensal (se aplicável)
        bool isRoyaltyBased;        // Se usa repagamento por royalties
        bool isCompleted;           // Se foi totalmente pago
        uint256 createdAt;
    }
    
    struct RoyaltyPayment {
        uint256 projectId;
        uint256 amount;
        string source;              // "spotify", "show", "merch", etc.
        uint256 timestamp;
        bytes32 oracleHash;         // Hash da verificação do oráculo
    }
    
    mapping(uint256 => RepaymentSchedule) public repaymentSchedules;
    mapping(uint256 => RoyaltyPayment[]) public royaltyPayments;
    mapping(uint256 => mapping(address => uint256)) public investorPayments; // Quanto cada investidor já recebeu
    mapping(uint256 => bool) public scheduleExists;
    
    // Pool de royalties por projeto
    mapping(uint256 => uint256) public royaltyPool;
    
    // Token para receber pagamentos (USDC/USDT)
    IERC20 public paymentToken;
    
    // Endereços autorizados para reportar royalties (oráculos)
    mapping(address => bool) public authorizedOracles;
    
    // Taxa de conversão royalty -> repagamento (base 10000)
    uint256 public royaltyConversionRate = 8000; // 80% das royalties vão para repagamento
    
    event RepaymentScheduleCreated(
        uint256 indexed projectId,
        uint256 totalOwed,
        bool isRoyaltyBased
    );
    
    event TraditionalPaymentMade(
        uint256 indexed projectId,
        address indexed payer,
        uint256 amount
    );
    
    event RoyaltyPaymentReceived(
        uint256 indexed projectId,
        uint256 amount,
        string source,
        address indexed oracle
    );
    
    event PaymentDistributed(
        uint256 indexed projectId,
        address indexed investor,
        uint256 amount
    );
    
    event RepaymentCompleted(
        uint256 indexed projectId,
        uint256 totalPaid
    );
    
    constructor(
        address _projectRegistry,
        address _loanEscrow,
        address _paymentToken,
        address _priceFeed
    ) {
        projectRegistry = IProjectRegistry(_projectRegistry);
        loanEscrow = ILoanEscrow(_loanEscrow);
        paymentToken = IERC20(_paymentToken);
        priceFeed = AggregatorV3Interface(_priceFeed);
    }
    
    modifier onlyArtist(uint256 _projectId) {
        IProjectRegistry.Project memory project = projectRegistry.getProject(_projectId);
        require(project.artist == msg.sender, "Apenas o artista pode executar");
        _;
    }
    
    modifier onlyAuthorizedOracle() {
        require(authorizedOracles[msg.sender], "Oraculo nao autorizado");
        _;
    }
    
    function scheduleRepayment(uint256 _projectId) external nonReentrant {
        require(!scheduleExists[_projectId], "Cronograma ja existe");
        
        IProjectRegistry.Project memory project = projectRegistry.getProject(_projectId);
        require(project.status == IProjectRegistry.ProjectStatus.ACTIVE, "Projeto deve estar ativo");
        
        (uint256 totalRaised, , bool fundsReleased, ,) = loanEscrow.getEscrowData(_projectId);
        require(fundsReleased, "Fundos devem ter sido liberados");
        
        // Calcular total devido (principal + juros)
        uint256 interest = (totalRaised * project.interestRate) / 10000;
        uint256 totalOwed = totalRaised + interest;
        
        RepaymentSchedule storage schedule = repaymentSchedules[_projectId];
        schedule.projectId = _projectId;
        schedule.totalOwed = totalOwed;
        schedule.totalPaid = 0;
        schedule.nextPaymentDue = block.timestamp + 30 days; // Primeiro pagamento em 30 dias
        schedule.isRoyaltyBased = project.allowsRoyaltyRepayment;
        schedule.isCompleted = false;
        schedule.createdAt = block.timestamp;
        
        if (!project.allowsRoyaltyRepayment) {
            // Repagamento tradicional em 12 parcelas
            schedule.monthlyPayment = totalOwed / 12;
        }
        
        scheduleExists[_projectId] = true;
        
        emit RepaymentScheduleCreated(_projectId, totalOwed, project.allowsRoyaltyRepayment);
    }
    
    function makeTraditionalPayment(uint256 _projectId, uint256 _amount) 
        external 
        nonReentrant 
        onlyArtist(_projectId)
    {
        RepaymentSchedule storage schedule = repaymentSchedules[_projectId];
        require(scheduleExists[_projectId], "Cronograma nao existe");
        require(!schedule.isCompleted, "Repagamento ja completado");
        require(_amount > 0, "Valor deve ser maior que zero");
        
        uint256 remainingDebt = schedule.totalOwed - schedule.totalPaid;
        uint256 paymentAmount = _amount > remainingDebt ? remainingDebt : _amount;
        
        // Transferir tokens do artista para o contrato
        paymentToken.safeTransferFrom(msg.sender, address(this), paymentAmount);
        
        schedule.totalPaid += paymentAmount;
        
        if (schedule.totalPaid >= schedule.totalOwed) {
            schedule.isCompleted = true;
            projectRegistry.updateProjectStatus(_projectId, IProjectRegistry.ProjectStatus.COMPLETED);
            emit RepaymentCompleted(_projectId, schedule.totalPaid);
        } else {
            // Atualizar próxima data de pagamento
            schedule.nextPaymentDue = block.timestamp + 30 days;
        }
        
        // Distribuir pagamento aos investidores
        _distributePayment(_projectId, paymentAmount);
        
        emit TraditionalPaymentMade(_projectId, msg.sender, paymentAmount);
    }
    
    function processRoyaltyPayment(
        uint256 _projectId,
        uint256 _amount,
        string memory _source,
        bytes32 _oracleHash
    ) external onlyAuthorizedOracle nonReentrant {
        require(scheduleExists[_projectId], "Cronograma nao existe");
        
        RepaymentSchedule storage schedule = repaymentSchedules[_projectId];
        require(!schedule.isCompleted, "Repagamento ja completado");
        require(schedule.isRoyaltyBased, "Projeto nao aceita repagamento por royalties");
        
        // Adicionar à lista de royalties recebidos
        royaltyPayments[_projectId].push(RoyaltyPayment({
            projectId: _projectId,
            amount: _amount,
            source: _source,
            timestamp: block.timestamp,
            oracleHash: _oracleHash
        }));
        
        // Calcular quanto vai para repagamento (80% por padrão)
        uint256 repaymentAmount = (_amount * royaltyConversionRate) / 10000;
        uint256 remainingDebt = schedule.totalOwed - schedule.totalPaid;
        
        if (repaymentAmount > remainingDebt) {
            repaymentAmount = remainingDebt;
        }
        
        schedule.totalPaid += repaymentAmount;
        royaltyPool[_projectId] += repaymentAmount;
        
        if (schedule.totalPaid >= schedule.totalOwed) {
            schedule.isCompleted = true;
            projectRegistry.updateProjectStatus(_projectId, IProjectRegistry.ProjectStatus.COMPLETED);
            emit RepaymentCompleted(_projectId, schedule.totalPaid);
        }
        
        // Distribuir pagamento aos investidores
        _distributePayment(_projectId, repaymentAmount);
        
        emit RoyaltyPaymentReceived(_projectId, _amount, _source, msg.sender);
    }
    
    function _distributePayment(uint256 _projectId, uint256 /* _totalAmount */) internal view {
        // obter totalRaised para calcular participações; se for zero, nada a distribuir
        (uint256 totalRaised, , , ,) = loanEscrow.getEscrowData(_projectId);
        if (totalRaised == 0) {
            return;
        }
        
        // Obter lista de investidores - precisaríamos implementar uma função no LoanEscrow para isso
        // Por simplicidade, vou assumir que temos essa informação
        
        // Esta função deveria iterar pelos investidores e distribuir proporcionalmente
        // Para o exemplo, vou mostrar a lógica conceitual
        /*
        for cada investidor {
            uint256 investorShare = (investimento * _totalAmount) / totalRaised;
            paymentToken.safeTransfer(investor, investorShare);
            investorPayments[_projectId][investor] += investorShare;
            emit PaymentDistributed(_projectId, investor, investorShare);
        }
        */
    }
    
    function calculateInterest(uint256 _projectId) external view returns (uint256) {
        IProjectRegistry.Project memory project = projectRegistry.getProject(_projectId);
        (uint256 totalRaised, , , ,) = loanEscrow.getEscrowData(_projectId);
        
        return (totalRaised * project.interestRate) / 10000;
    }
    
    function getRepaymentStatus(uint256 _projectId) 
        external 
        view 
        returns (
            uint256 totalOwed,
            uint256 totalPaid,
            uint256 remainingDebt,
            bool isCompleted,
            uint256 nextPaymentDue
        ) 
    {
        RepaymentSchedule memory schedule = repaymentSchedules[_projectId];
        return (
            schedule.totalOwed,
            schedule.totalPaid,
            schedule.totalOwed - schedule.totalPaid,
            schedule.isCompleted,
            schedule.nextPaymentDue
        );
    }
    
    function getRoyaltyPayments(uint256 _projectId) 
        external 
        view 
        returns (RoyaltyPayment[] memory) 
    {
        return royaltyPayments[_projectId];
    }
    
    function getInvestorPayments(uint256 _projectId, address _investor) 
        external 
        view 
        returns (uint256) 
    {
        return investorPayments[_projectId][_investor];
    }
    
    // Funções administrativas
    function addAuthorizedOracle(address _oracle) external onlyOwner {
        authorizedOracles[_oracle] = true;
    }
    
    function removeAuthorizedOracle(address _oracle) external onlyOwner {
        authorizedOracles[_oracle] = false;
    }
    
    function setRoyaltyConversionRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 10000, "Taxa maxima 100%");
        royaltyConversionRate = _newRate;
    }
    
    function setPaymentToken(address _newToken) external onlyOwner {
        paymentToken = IERC20(_newToken);
    }
    
    // Função de emergência para pausar repagamentos
    function emergencyPauseRepayment(uint256 _projectId) external onlyOwner {
        repaymentSchedules[_projectId].isCompleted = true;
    }
}

contracts/ReputationManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface IProjectRegistry {
    enum ProjectStatus { 
        PENDING, APPROVED, FUNDING, FUNDED, ACTIVE, COMPLETED, CANCELLED 
    }
    
    function getProject(uint256 _projectId) external view returns (
        uint256 id,
        address artist,
        string memory title,
        string memory description,
        string memory demoUri,
        uint256 fundingGoal,
        uint256 fundingDeadline,
        uint256 repaymentDeadline,
        uint256 interestRate,
        ProjectStatus status,
        bool allowsRoyaltyRepayment,
        uint256 createdAt,
        uint256 approvedAt
    );
}

interface IRepaymentManager {
    function getRepaymentStatus(uint256 _projectId) 
        external 
        view 
        returns (
            uint256 totalOwed,
            uint256 totalPaid,
            uint256 remainingDebt,
            bool isCompleted,
            uint256 nextPaymentDue
        );
}

contract ReputationManager is Ownable, ReentrancyGuard {
    IProjectRegistry public projectRegistry;
    IRepaymentManager public repaymentManager;
    
    struct ReputationData {
        uint256 score;              // Score atual (0-1000)
        uint256 projectsCompleted;  // Projetos finalizados
        uint256 projectsTotal;      // Total de projetos
        uint256 onTimePayments;     // Pagamentos pontuais
        uint256 latePayments;       // Pagamentos atrasados
        uint256 totalRaised;        // Total captado historicamente
        uint256 lastUpdated;        // Última atualização
        bool isVerified;            // Verificação manual da equipe
    }
    
    struct DeliveryRecord {
        uint256 projectId;
        string deliverableType;     // "demo", "album", "video", "show"
        string ipfsHash;           // Hash IPFS da entrega
        uint256 timestamp;
        bool isOnTime;             // Se foi entregue no prazo
        uint256 qualityScore;      // Avaliação da qualidade (1-100)
        bytes32 offChainDataHash;  // Hash dos dados off-chain
    }
    
    struct SocialMetrics {
        uint256 followersCount;
        uint256 engagementRate;    // Taxa de engajamento (base 10000)
        uint256 streamingPlays;    // Plays em plataformas de streaming
        uint256 lastSocialUpdate;
        string[] verifiedPlatforms; // "spotify", "instagram", "youtube", etc.
    }
    
    mapping(address => ReputationData) public artistReputation;
    mapping(address => DeliveryRecord[]) public deliveryHistory;
    mapping(address => SocialMetrics) public socialMetrics;
    mapping(address => bool) public artistExists;
    
    // Oráculos autorizados para atualizar dados sociais
    mapping(address => bool) public socialOracles;
    
    // Pesos para cálculo de reputação (base 10000 = 100%)
    uint256 public deliveryWeight = 4000;      // 40%
    uint256 public qualityWeight = 2500;       // 25%
    uint256 public paymentWeight = 2000;       // 20%
    uint256 public engagementWeight = 1000;    // 10%
    uint256 public transparencyWeight = 500;   // 5%
    
    // Bônus por consistência
    uint256 public consistencyBonus = 200;     // Até 20% de bônus
    
    // Score mínimo para aprovação automática
    uint256 public minimumApprovalScore = 600; // 60%
    
    event ReputationUpdated(
        address indexed artist,
        uint256 oldScore,
        uint256 newScore,
        string reason
    );
    
    event DeliveryRecorded(
        address indexed artist,
        uint256 indexed projectId,
        string deliverableType,
        bool isOnTime,
        uint256 qualityScore
    );
    
    event SocialMetricsUpdated(
        address indexed artist,
        uint256 followers,
        uint256 engagementRate,
        address indexed oracle
    );
    
    event ArtistVerified(
        address indexed artist,
        address indexed verifier
    );
    
    constructor(
        address _projectRegistry,
        address _repaymentManager
    ) {
        projectRegistry = IProjectRegistry(_projectRegistry);
        repaymentManager = IRepaymentManager(_repaymentManager);
    }
    
    modifier onlyAuthorizedOracle() {
        require(socialOracles[msg.sender] || msg.sender == owner(), "Oracle nao autorizado");
        _;
    }
    
    function initializeArtist(address _artist) external {
        require(!artistExists[_artist], "Artista ja existe");
        
        artistReputation[_artist] = ReputationData({
            score: 500,           // Score inicial médio
            projectsCompleted: 0,
            projectsTotal: 0,
            onTimePayments: 0,
            latePayments: 0,
            totalRaised: 0,
            lastUpdated: block.timestamp,
            isVerified: false
        });
        
        artistExists[_artist] = true;
    }
    
    function recordDelivery(
        address _artist,
        uint256 _projectId,
        string memory _deliverableType,
        string memory _ipfsHash,
        bool _isOnTime,
        uint256 _qualityScore,
        bytes32 _offChainDataHash
    ) external onlyOwner {
        require(artistExists[_artist], "Artista nao existe");
        require(_qualityScore >= 1 && _qualityScore <= 100, "Score qualidade invalido");
        
        deliveryHistory[_artist].push(DeliveryRecord({
            projectId: _projectId,
            deliverableType: _deliverableType,
            ipfsHash: _ipfsHash,
            timestamp: block.timestamp,
            isOnTime: _isOnTime,
            qualityScore: _qualityScore,
            offChainDataHash: _offChainDataHash
        }));
        
        // Atualizar contadores de projetos
        ReputationData storage rep = artistReputation[_artist];
        rep.projectsTotal++;
        
        // Verificar se projeto foi completado para atualizar contadores
        (, , , bool isCompleted,) = repaymentManager.getRepaymentStatus(_projectId);
        if (isCompleted) {
            rep.projectsCompleted++;
        }
        
        emit DeliveryRecorded(_artist, _projectId, _deliverableType, _isOnTime, _qualityScore);
        
        // Recalcular score
        _updateReputationScore(_artist);
    }
    
    function updateSocialMetrics(
        address _artist,
        uint256 _followersCount,
        uint256 _engagementRate,
        uint256 _streamingPlays,
        string[] memory _verifiedPlatforms
    ) external onlyAuthorizedOracle {
        require(artistExists[_artist], "Artista nao existe");
        require(_engagementRate <= 10000, "Taxa engajamento invalida");
        
        SocialMetrics storage metrics = socialMetrics[_artist];
        metrics.followersCount = _followersCount;
        metrics.engagementRate = _engagementRate;
        metrics.streamingPlays = _streamingPlays;
        metrics.lastSocialUpdate = block.timestamp;
        metrics.verifiedPlatforms = _verifiedPlatforms;
        
        emit SocialMetricsUpdated(_artist, _followersCount, _engagementRate, msg.sender);
        
        // Recalcular score
        _updateReputationScore(_artist);
    }
    
    function updatePaymentMetrics(address _artist, uint256 _projectId) external {
        require(artistExists[_artist], "Artista nao existe");
        
        ReputationData storage rep = artistReputation[_artist];
        
        // Verificar status do repagamento
        (, , , bool isCompleted, uint256 nextPaymentDue) = repaymentManager.getRepaymentStatus(_projectId);
        
        if (isCompleted) {
            rep.onTimePayments++;
        } else if (block.timestamp > nextPaymentDue) {
            rep.latePayments++;
        }
        
        // Recalcular score
        _updateReputationScore(_artist);
    }
    
    function _updateReputationScore(address _artist) internal {
        ReputationData storage rep = artistReputation[_artist];
        uint256 oldScore = rep.score;
        
        // 1. Score de entregas (40%)
        uint256 deliveryScore = 0;
        if (rep.projectsTotal > 0) {
            deliveryScore = (rep.projectsCompleted * 1000) / rep.projectsTotal;
        } else {
            deliveryScore = 500; // Score neutro para novos artistas
        }
        
        // 2. Score de qualidade (25%)
        uint256 qualityScore = _calculateQualityScore(_artist);
        
        // 3. Score de pagamentos (20%)
        uint256 paymentScore = 0;
        uint256 totalPayments = rep.onTimePayments + rep.latePayments;
        if (totalPayments > 0) {
            paymentScore = (rep.onTimePayments * 1000) / totalPayments;
        } else {
            paymentScore = 500; // Score neutro
        }
        
        // 4. Score de engajamento (10%)
        uint256 engagementScore = _calculateEngagementScore(_artist);
        
        // 5. Score de transparência (5%)
        uint256 transparencyScore = _calculateTransparencyScore(_artist);
        
        // Calcular score final
        uint256 newScore = (
            (deliveryScore * deliveryWeight) +
            (qualityScore * qualityWeight) +
            (paymentScore * paymentWeight) +
            (engagementScore * engagementWeight) +
            (transparencyScore * transparencyWeight)
        ) / 10000;
        
        // Aplicar bônus de consistência
        uint256 consistency = _calculateConsistencyBonus(_artist);
        newScore = newScore + ((newScore * consistency) / 10000);
        
        // Garantir que não ultrapasse 1000
        if (newScore > 1000) {
            newScore = 1000;
        }
        
        rep.score = newScore;
        rep.lastUpdated = block.timestamp;
        
        emit ReputationUpdated(_artist, oldScore, newScore, "Score recalculado");
    }
    
    function _calculateQualityScore(address _artist) internal view returns (uint256) {
        DeliveryRecord[] memory deliveries = deliveryHistory[_artist];
        if (deliveries.length == 0) return 500;
        
        uint256 totalQuality = 0;
        for (uint256 i = 0; i < deliveries.length; i++) {
            totalQuality += deliveries[i].qualityScore;
        }
        
        return (totalQuality * 10) / deliveries.length; // Converter para escala 0-1000
    }
    
    function _calculateEngagementScore(address _artist) internal view returns (uint256) {
        SocialMetrics memory metrics = socialMetrics[_artist];
        
        // Score baseado em engajamento e seguidores
        uint256 engagementFactor = metrics.engagementRate * 100; // Converter para escala 0-1000
        uint256 followerFactor = 0;
        
        if (metrics.followersCount > 100000) {
            followerFactor = 1000;
        } else if (metrics.followersCount > 10000) {
            followerFactor = 800;
        } else if (metrics.followersCount > 1000) {
            followerFactor = 600;
        } else {
            followerFactor = 400;
        }
        
        return (engagementFactor + followerFactor) / 2;
    }
    
    function _calculateTransparencyScore(address _artist) internal view returns (uint256) {
        SocialMetrics memory metrics = socialMetrics[_artist];
        DeliveryRecord[] memory deliveries = deliveryHistory[_artist];
        
        uint256 score = 500; // Base
        
        // Bônus por plataformas verificadas
        score += metrics.verifiedPlatforms.length * 100;
        
        // Bônus por entregas documentadas
        score += deliveries.length * 50;
        
        // Bônus por verificação manual
        if (artistReputation[_artist].isVerified) {
            score += 200;
        }
        
        return score > 1000 ? 1000 : score;
    }
    
    function _calculateConsistencyBonus(address _artist) internal view returns (uint256) {
        ReputationData memory rep = artistReputation[_artist];
        
        if (rep.projectsTotal < 3) return 0; // Precisa de pelo menos 3 projetos
        
        uint256 successRate = (rep.projectsCompleted * 100) / rep.projectsTotal;
        
        if (successRate >= 90) return consistencyBonus; // 20% bônus
        if (successRate >= 80) return consistencyBonus * 75 / 100; // 15% bônus
        if (successRate >= 70) return consistencyBonus * 50 / 100; // 10% bônus
        
        return 0;
    }
    
    function verifyArtist(address _artist) external onlyOwner {
        require(artistExists[_artist], "Artista nao existe");
        
        artistReputation[_artist].isVerified = true;
        emit ArtistVerified(_artist, msg.sender);
        
        // Recalcular score com bônus de verificação
        _updateReputationScore(_artist);
    }
    
    function canAutoApprove(address _artist) external view returns (bool) {
        if (!artistExists[_artist]) return false;
        return artistReputation[_artist].score >= minimumApprovalScore;
    }
    
    // Getters
    function getReputationData(address _artist) 
        external 
        view 
        returns (ReputationData memory) 
    {
        return artistReputation[_artist];
    }
    
    function getDeliveryHistory(address _artist) 
        external 
        view 
        returns (DeliveryRecord[] memory) 
    {
        return deliveryHistory[_artist];
    }
    
    function getSocialMetrics(address _artist) 
        external 
        view 
        returns (SocialMetrics memory) 
    {
        return socialMetrics[_artist];
    }
    
    // Funções administrativas
    function addSocialOracle(address _oracle) external onlyOwner {
        socialOracles[_oracle] = true;
    }
    
    function removeSocialOracle(address _oracle) external onlyOwner {
        socialOracles[_oracle] = false;
    }
    
    function setWeights(
        uint256 _delivery,
        uint256 _quality,
        uint256 _payment,
        uint256 _engagement,
        uint256 _transparency
    ) external onlyOwner {
        require(
            _delivery + _quality + _payment + _engagement + _transparency == 10000,
            "Pesos devem somar 100%"
        );
        
        deliveryWeight = _delivery;
        qualityWeight = _quality;
        paymentWeight = _payment;
        engagementWeight = _engagement;
        transparencyWeight = _transparency;
    }
    
    function setMinimumApprovalScore(uint256 _newScore) external onlyOwner {
        require(_newScore <= 1000, "Score maximo 1000");
        minimumApprovalScore = _newScore;
    }
}

